mapper.select:
SELECT
email
FROM
users
WHERE
name=#{name}
AND
phone_head=#{phoneHead}
AND
phone_body=#{phoneBody}
AND
phone_tail=#{phoneTail}

SELECT
password
FROM
users
WHERE
email = #{email}
AND
name =
#{name}
AND
phone_head = #{phoneHead}
AND
phone_body = #{phoneBody}
AND
phone_tail = #{phoneTail}

SELECT * FROM (SELECT ROW_NUMBER () OVER (
ORDER BY register_time DESC
ORDER BY #{orderBy}
) ,id, nickname, name, email ,phone_head, phone_body, phone_tail ,profile_img, register_time, user_role FROM users
user_role = 'u'
AND ${searchCondition} like '%' ||#{searchKeyword}|| '%'
) originalsql WHERE ROW_NUMBER BETWEEN #{firstRow} AND #{lastRow}
